SELECT COUNT(*) as count,DATE(receive_date) as receive_date, contact_type,  financial_type_id as financial_type, SUM(total_amount) as total, payment_instrument_id as instrument from civicrm_contribution JOIN civicrm_contact as contact ON contact_id=contact.id  WHERE receive_date is not null AND ( is_test = 0 OR is_test = NULL) AND receive_date <> '0000-00-00' group by DATE(receive_date),contact.contact_type, instrument,financial_type_id;
